const navbar=document.getElementById('navbar');window.addEventListener('scroll',()=>{if(window.scrollY>0){navbar.classList.add('bg-white','shadow-xl');navbar.classList.remove('bg-transparent','shadow-lg')}else{navbar.classList.add('bg-transparent','shadow-lg');navbar.classList.remove('bg-white','shadow-xl','shadow-lg')}});$(document).ready(function(){if(window.scrollY>0){navbar.classList.add('bg-white','shadow-xl');navbar.classList.remove('bg-transparent','shadow-lg')}else{navbar.classList.add('bg-transparent','shadow-lg');navbar.classList.remove('bg-white','shadow-xl','shadow-lg')}})
const navLinks=document.querySelectorAll('.nav-link');const sections=document.querySelectorAll('section');const observer=new IntersectionObserver((entries)=>{entries.forEach(entry=>{const id=entry.target.getAttribute('id');const navItem=document.querySelector(`.nav-link[href="#${id}"]`);if(entry.isIntersecting&&navItem!==null){navLinks.forEach(link=>link.classList.remove('active'));navItem.classList.add('active')}})},{root:null,rootMargin:'0px',threshold:0.6});sections.forEach(section=>{observer.observe(section)});document.addEventListener("DOMContentLoaded",function(){const toggleBtn=document.querySelector("[data-collapse-toggle]");const navbarWrapper=document.getElementById("navbar");const navbarMenu=document.getElementById("menu");toggleBtn.addEventListener("click",function(){setTimeout(()=>{if(!navbarMenu.classList.contains("hidden")){navbarWrapper.classList.add("bg-white","text-black","shadow-xl")}else if(navbarMenu.classList.contains("hidden")&&window.scrollY<=0){navbarWrapper.classList.remove("bg-white","text-black","shadow-xl")}},10)})})